<?phpfunction cwp_taxonomy_dropdown_meta_box( $post, $box ) {	$tax_name = esc_attr(substr($box['id'], 34));	$taxonomy = get_taxonomy($tax_name);	$terms = wp_get_object_terms( $post->ID, $taxonomy->name );	$term = $terms[0];		cwp_dropdown_taxonomy( $taxonomy->name, "name=taxonomy_dropdown[" . $taxonomy->name . ']&hide_empty=0&selected=' . $term->term_id . '&show_option_all=Select ' . ucfirst( $taxonomy->name ) );		?>	<p>Add New <?php echo ucfirst( $taxonomy->name ); ?></p>		<span class="">	    <label class="screen-reader-text" for="new-tag-<?php echo $tax_name; ?>"><?php echo $box['title']; ?></label>	    <input type="text" id="" name="newtaxterm[<?php echo $tax_name; ?>]" class="newtag form-input-tip" size="16" autocomplete="off" value="" />	</span>			<?php	}function cwp_taxonomy_dropdown_meta_box_submitted( $post, $page ) {		if( $_POST['taxonomy_dropdown'] ) {				foreach( (array) $_POST['taxonomy_dropdown'] as $tax => $terms ) {			$terms = (array) $terms;			foreach( (array) $terms as $key => $term ) {				$terms[$key] = (int) $term;			}			wp_set_object_terms( $post->ID, $terms, $tax );		}	}	//create a new term if one was added	if( $_POST['newtaxterm'] ) {		foreach( (array) $_POST['newtaxterm'] as $tax => $terms ) {			if( trim($terms) && !empty( $terms ) )				wp_set_object_terms( $post->ID, $terms, $tax );		}	}	if( !$_POST['newtaxterm'] && !$_POST['taxonomy_dropdown'] && $page->args['default'] ) {		wp_set_object_terms( $post->ID, $page->args['deafault'], $tax );	}}?>