<?php/** * Display post tags form fields. * * @since 2.6.0 * * @param object $post */function cwp_taxonomy_meta_box($post, $box) {	$tax_name = esc_attr(substr($box['id'], 25));	$taxonomy = get_taxonomy($tax_name);	$helps = isset($taxonomy->helps) ? esc_attr($taxonomy->helps) : __('Separate ' . $tax_name . 's with commas.');	?>	<div class="tagsdiv" id="<?php echo $tax_name; ?>">		<div class="jaxtag">		<div class="nojs-tags hide-if-js">		<p><?php _e('Add or remove tags'); ?></p>		<textarea name="<?php echo "tax_input[$tax_name]"; ?>" class="the-tags" id="tax-input[<?php echo $tax_name; ?>]"><?php echo esc_attr(get_terms_to_edit( $post->ID, $tax_name )); ?></textarea></div>			<span class="ajaxtag hide-if-no-js">			<label class="screen-reader-text" for="new-tag-<?php echo $tax_name; ?>"><?php echo $box['title']; ?></label>			<input type="text" id="new-tag-<?php echo $tax_name; ?>" name="newtag[<?php echo $tax_name; ?>]" class="newtag form-input-tip" size="16" autocomplete="off" value="<?php esc_attr_e('Add new ' . $tax_name); ?>" />			<input type="button" class="button tagadd" value="<?php esc_attr_e('Add'); ?>" tabindex="3" />		</span></div>		<p class="howto"><?php echo $helps; ?></p>		<div class="tagchecklist"></div>	</div>	<p class="tagcloud-link hide-if-no-js"><a href="#titlediv" class="tagcloud-link" id="link-<?php echo $tax_name; ?>"><?php printf( __('Choose from the most used ' . $tax_name . 's in %s'), $box['title'] ); ?></a></p>	<?php}function cwp_taxonomy_meta_box_submitted( $post ) {	foreach( $_POST['tax_input'] as $tax => $tax_terms ) {		wp_set_object_terms( $post->ID, explode( ',', (string)$tax_terms ), $tax );	}}?>